contracts/PrimordialLPStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IPrimordialPePe.sol";
import "./RewardBooster.sol";
import "hardhat/console.sol";

contract PrimordialLPStaking is Ownable, ReentrancyGuard {
    IERC20 public primordialLPToken;
    address public primordialplanetsAddress;
    address public cosmicimpactsAddress;
    IPrimordialPePe public primordialPePeToken;
    BoostedRewardCalculator public boostedRewardCalculator;

    struct StakerInfo {
        uint256 stakedAmount;
        bool lpStaked;
        bool nftStaked;
    }

    mapping(address => StakerInfo) public stakers;
    using EnumerableSet for EnumerableSet.UintSet;
    mapping(address => EnumerableSet.UintSet) private stakedNFTs;
    mapping(address => address) public nftAddress;

    mapping(address => uint256) public lpStakeTimes;
    mapping(address => uint256) public lpClaimTimes;

    event StakeLP(address indexed user, uint256 amount);
    event Claimed(address indexed user, uint256 rewardAmount);
    event StakeNFT(address indexed user, address indexed nftAddress, uint256 tokenId);

    constructor(address _primordialLPAddress, 
                address _primordialPePeAddress) {
        primordialLPToken = IERC20(_primordialLPAddress);
        primordialPePeToken = IPrimordialPePe(_primordialPePeAddress);
        boostedRewardCalculator = BoostedRewardCalculator(address(0));
    }

    function stakeLPToken(uint256 amount) external {
        require(amount > 0, "Cannot stake 0");
        _stakeLPToken(msg.sender, amount);
    }

    function unstakeLPToken(uint256 _stakedAmount) public {
        require(stakers[msg.sender].stakedAmount > 0, "No staked LP tokens");
        _unstakeLPToken(_stakedAmount, msg.sender);
    }

    function _stakeLPToken(address stakerAddress, uint256 amount) internal onlyLPOwner nonReentrant {
        StakerInfo storage staker = stakers[stakerAddress];
        require(amount > 0, "Cannot stake 0");

        primordialLPToken.transferFrom(stakerAddress, address(this), amount);
        staker.stakedAmount += amount;
        lpStakeTimes[stakerAddress] = block.timestamp;

        if (!staker.lpStaked) {
            lpClaimTimes[stakerAddress] = 0;
            staker.lpStaked = true;
        }

        emit StakeLP(stakerAddress, amount);
    }

    function _unstakeLPToken(uint256 _stakedAmount, address _staker) internal onlyLPStaker nonReentrant {
        StakerInfo storage staker = stakers[_staker];
        require(staker.stakedAmount > 0, "No staked LP tokens");

        uint256 primordialOwed;
        if (staker.nftStaked) {
            primordialOwed = calculateBoostedPrimordial(_stakedAmount, _staker);
        } else {
            primordialOwed = getBasePrimordialOwed(_stakedAmount, _staker);
        }

        primordialPePeToken.mint(_staker, primordialOwed);
        primordialLPToken.transfer(_staker, staker.stakedAmount);
        staker.stakedAmount -= _stakedAmount;

        if (staker.stakedAmount == 0) {
        staker.lpStaked = false;
        }
        lpClaimTimes[_staker] = block.timestamp;

        emit Claimed(_staker, primordialOwed);
    }

    function stakeNFTById(uint256 _nftId) external {
        require(!stakers[msg.sender].nftStaked, "Already staked an NFT");
        _stakeNFT(_nftId);
    }

    function unstakeNFTById(uint256 _nftId) public {
        require(stakers[msg.sender].nftStaked, "No staked NFT");
        _unstakeNFT(_nftId);
    }

    function _stakeNFT(uint256 _nftId) internal onlyNFTOwner(_nftId) nonReentrant {
        require(!stakers[msg.sender].nftStaked, "Already staked with an NFT");

        address collectionAddress;
        if (IERC721Enumerable(primordialplanetsAddress).ownerOf(_nftId) == msg.sender) {
            collectionAddress = primordialplanetsAddress;
        } else if (IERC721Enumerable(cosmicimpactsAddress).ownerOf(_nftId) == msg.sender) {
            collectionAddress = cosmicimpactsAddress;
        } else {
            revert("NFT does not belong to a supported collection");
        }

        nftAddress[msg.sender] = collectionAddress;

        IERC721Enumerable(collectionAddress).transferFrom(msg.sender, address(this), _nftId);
        stakedNFTs[msg.sender].add(_nftId);
        stakers[msg.sender].nftStaked = true;
        emit StakeNFT(msg.sender, collectionAddress, _nftId);
    }

    function _unstakeNFT(uint256 _nftId) internal onlyNFTStaker(_nftId) nonReentrant {
        require(stakers[msg.sender].nftStaked, "No staked NFT");
        address _nftAddress = nftAddress[msg.sender];
        require(_nftAddress == primordialplanetsAddress || _nftAddress == cosmicimpactsAddress, "Invalid NFT collection");

        IERC721Enumerable(_nftAddress).transferFrom(address(this), msg.sender, _nftId);
        stakedNFTs[msg.sender].remove(_nftId);
        nftAddress[msg.sender] = address(0);
        stakers[msg.sender].nftStaked = false;
    }

    function claimReward(uint256 _stakedAmount) external nonReentrant {
        console.log("Claiming reward for address:", msg.sender);
        StakerInfo storage staker = stakers[msg.sender];
        uint256 reward;
        if (staker.nftStaked) {
            console.log("Calculating boosted reward");
            reward = calculateBoostedPrimordial(_stakedAmount, msg.sender);
            console.log("Boosted reward:", reward);
        } else {
            reward = getBasePrimordialOwed(_stakedAmount, msg.sender);
        }

        require(reward > 0, "No rewards available");
        console.log("Minting reward");
        primordialPePeToken.mint(msg.sender, reward);
        console.log("Reward minted");
        console.log("reward minted:", reward);
        lpClaimTimes[msg.sender] = block.timestamp;
        emit Claimed(msg.sender, reward);
    }

    function calculateBoostedPrimordial(uint256 _stakedAmount, address _staker) public view returns (uint256) {
        StakerInfo storage staker = stakers[_staker];
        uint256 elapsedTime = block.timestamp - lpStakeTimes[_staker];
        uint256 elapsedDays = elapsedTime < 1 days ? 0 : elapsedTime / 1 days;
        uint256 leftoverSeconds = elapsedTime - elapsedDays * 1 days;

        console.log("Elapsed Time:", elapsedTime);
        console.log("Elapsed Days:", elapsedDays);
        console.log("Leftover Seconds:", leftoverSeconds);

        uint256 boostPercentage = 0;
        if (staker.nftStaked) {
            uint256 stakedNFTId = stakedNFTs[_staker].at(0);
            address nftCollectionAddress = nftAddress[_staker];
            boostPercentage = boostedRewardCalculator.getBoostPercentage(stakedNFTId, nftCollectionAddress);

            console.log("Staked NFT ID:", stakedNFTId);
            console.log("NFT Collection Address:", nftCollectionAddress);
            console.log("Boost Percentage:", boostPercentage);
        }

        if (lpClaimTimes[_staker] == 0) {
            return _calculatePrimordialWithBoost(_stakedAmount, elapsedDays, leftoverSeconds, boostPercentage);
        }

        uint256 elapsedTimeSinceClaim = lpClaimTimes[_staker] -
            lpStakeTimes[_staker];
        uint256 elapsedDaysSinceClaim = elapsedTimeSinceClaim < 1 days
            ? 0
            : elapsedTimeSinceClaim / 1 days;
        uint256 leftoverSecondsSinceClaim = elapsedTimeSinceClaim -
            elapsedDaysSinceClaim *
            1 days;

        return
            _calculatePrimordialWithBoost(_stakedAmount, elapsedDays, leftoverSeconds, boostPercentage) - 
            _calculatePrimordialWithBoost(_stakedAmount, elapsedDaysSinceClaim, leftoverSecondsSinceClaim, boostPercentage);        
    }

    function getBasePrimordialOwed(uint256 _stakedAmount, address _staker) public view returns (uint256) {
        uint256 elapsedTime = block.timestamp - lpStakeTimes[_staker];
        uint256 elapsedDays = elapsedTime < 1 days ? 0 : elapsedTime / 1 days;
        uint256 leftoverSeconds = elapsedTime - elapsedDays * 1 days;

        if (lpClaimTimes[_staker] == 0) {
            return _calculatePrimordial(_stakedAmount, elapsedDays, leftoverSeconds);
        }

        uint256 elapsedTimeSinceClaim = lpClaimTimes[_staker] -
            lpStakeTimes[_staker];
        uint256 elapsedDaysSinceClaim = elapsedTimeSinceClaim < 1 days
            ? 0
            : elapsedTimeSinceClaim / 1 days;
        uint256 leftoverSecondsSinceClaim = elapsedTimeSinceClaim -
            elapsedDaysSinceClaim *
            1 days;

        return
            _calculatePrimordial(_stakedAmount, elapsedDays, leftoverSeconds) -
            _calculatePrimordial(_stakedAmount, elapsedDaysSinceClaim, leftoverSecondsSinceClaim);
    }

    function getStakerInfo(address _staker) 
        public 
        view 
        returns (
            uint256 stakedAmount, 
            bool nftStaked, 
            uint256 stakedNFTId, 
            uint256 lpStakeTime, 
            uint256 lpClaimTime
        ) 
        {
        StakerInfo storage staker = stakers[_staker];
        stakedAmount = staker.stakedAmount;
        nftStaked = staker.nftStaked;

        stakedNFTId = 0;
        if (nftStaked) {
            EnumerableSet.UintSet storage stakedNFTSet = stakedNFTs[_staker];
            if (stakedNFTSet.length() > 0) {
                stakedNFTId = stakedNFTSet.at(0);
            }
        }

        lpStakeTime = lpStakeTimes[_staker];
        lpClaimTime = lpClaimTimes[_staker];
    }

    function isNFTStaked(address _user, uint256 _nftId) public view returns (bool) {
        return stakedNFTs[_user].contains(_nftId);
    }

    function getLockedNFTInfo(address _staker) public view returns (address nftCollectionAddress, bool isNFTStaked, uint256 stakedNFTId) {
            nftCollectionAddress = nftAddress[_staker]; // Address of the NFT collection
            isNFTStaked = stakers[_staker].nftStaked; // Check if any NFT is staked

            // Initialize stakedNFTId to an arbitrary value, say max uint256
            stakedNFTId = type(uint256).max; // Indicates no NFT is staked

        if (isNFTStaked && stakedNFTs[_staker].length() > 0) {
            stakedNFTId = stakedNFTs[_staker].at(0); // Retrieve the ID of the staked NFT
        }
    }

    function getPrimordialEmission(address _address) public view returns (uint256) {
        uint256 elapsedTime = block.timestamp - lpStakeTimes[_address];
        uint256 elapsedDays = elapsedTime < 1 days ? 0 : elapsedTime / 1 days;
        return _primordialDailyIncrement(elapsedDays);
    }

    function _calculatePrimordialWithBoost(uint256 _stakedAmount, uint256 _days, uint256 _leftoverSeconds, uint256 _boostPercentage) internal pure returns (uint256) {
        uint256 totalReward = _calculatePrimordial(_stakedAmount, _days, _leftoverSeconds);
        console.log("Total reward before boost:", totalReward);

        // Adjusted formula to avoid rounding errors
        uint256 boostedReward = (totalReward * (100 + _boostPercentage)) / 100;
        console.log("Boosted reward:", boostedReward);

        return boostedReward;
    }

    function _calculatePrimordial(uint256 _stakedAmount, uint256 _days, uint256 _leftoverSeconds) internal pure returns (uint256) {
        uint256 totalReward = 0;

        // Calculate total reward for the number of days
        if (_days > 0) {
            uint256 dailyIncrementBasisPoints = _primordialDailyIncrement(_days);
            totalReward = (_stakedAmount * dailyIncrementBasisPoints * _days) / 1000000; // Dividing by 1000000 to convert basis points to percentage
        }

        // Calculate reward for leftover seconds
        if (_leftoverSeconds > 0) {
            uint256 finalDayIncrement = _primordialDailyIncrement(_days + 1);
            uint256 rewardPerSecond = (_stakedAmount * finalDayIncrement) / (1000000 * 1 days);
            totalReward += rewardPerSecond * _leftoverSeconds;
        }

        return totalReward;
    }

    function _primordialDailyIncrement(uint256 _days)
        internal
        pure
        returns (uint256)
        {
        if (_days > 100) {
            _days = 100;
        }
        // Calculate daily increment: 0.005% per day, increasing each day
        return 50 + (_days - 1) * 75; // Each increment is in basis points (0.001% = 1 basis point)
    }

    function setBoostedRewardCalculator(address _boostedRewardCalculator) public onlyOwner {
        boostedRewardCalculator = BoostedRewardCalculator(_boostedRewardCalculator);
    }

    function setPrimordialPlanetsContract(address _primordialplanetsAddress) public onlyOwner {
        primordialplanetsAddress = _primordialplanetsAddress;
    }

    function setCosmicImpactContract(address _cosmicimpactsAddress) public onlyOwner {
        cosmicimpactsAddress = _cosmicimpactsAddress;
    }

    function setPrimordialLPToken(address _primordialLPAddress) public onlyOwner {
        require(_primordialLPAddress != address(0), "Address cannot be zero");
        primordialLPToken = IERC20(_primordialLPAddress);
    }

    function setPrimordialPePe(address _primordialPePeAddress) public onlyOwner {
        require(_primordialPePeAddress != address(0), "Address cannot be zero");
        primordialPePeToken = IPrimordialPePe(_primordialPePeAddress);
    }

    function toggleRewardBooster(bool _enabled) external onlyOwner {
        console.log("BoostedRewardCalculator address;", address(boostedRewardCalculator));
        if (_enabled) {
            require(address(boostedRewardCalculator) != address(0), "Reward booster address not set");
        }
        boostedRewardCalculator = _enabled ? boostedRewardCalculator : BoostedRewardCalculator(address(0));
    }

    function isRewardBoosterEnabled() public view returns (bool) {
        return address(boostedRewardCalculator) != address(0);
    }

    modifier onlyLPOwner() {
        require(primordialLPToken.balanceOf(msg.sender) > 0, "Not an LP owner");
        _;
    }

    modifier onlyLPStaker() {
        require(stakers[msg.sender].stakedAmount > 0, "Not an LP staker");
        _;
    }

    modifier onlyNFTOwner(uint256 _nftId) {
        require(
            IERC721Enumerable(primordialplanetsAddress).ownerOf(_nftId) == msg.sender ||
            IERC721Enumerable(cosmicimpactsAddress).ownerOf(_nftId) == msg.sender,
            "Can only stake NFTs you own!"
        );
        _;
    }

    modifier onlyNFTStaker(uint256 _nftId) {
        require(
            stakedNFTs[msg.sender].contains(_nftId),
            "Can only unstake NFTs locked in your pool!"
        );
        _;
    }

}

contracts/PrimordialPePe.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title PrimordialPePe
 * @dev Contract to manage staking and rewards for Primordial PePe tokens.
 */
contract PrimordialPePe is ERC20Burnable, Ownable, AccessControl {

    struct StakedPlanet {
        uint256 tokenId;
        uint256 stakedSince;
        uint256 primordialEmission;
    }

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private minters;
    EnumerableSet.AddressSet private admins;

    using EnumerableSet for EnumerableSet.UintSet;
    uint256 public constant STAKE_LIMIT = 25;

    bool public minable = false;
    address public allowed_pepe_miner;
    address public allowed_pond_miner;
    uint256 public max_rig_supply = 420690000000000000000000000000000;

    address public primordialplanetsAddress;
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    //User to staked planets
    mapping(address => EnumerableSet.UintSet) private stakedPlanets;
    //Staked Planet to timestamp staked
    mapping(uint256 => uint256) public planetStakeTimes;
    mapping(uint256 => uint256) public planetClaimTimes;

   constructor() ERC20("PrimordialPePe", "PPEPE") {

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);

        minters.add(msg.sender);
        admins.add(msg.sender);
    }

    /**
     * @notice Stake specified planet IDs.
     * @param _planetIds The planet IDs to stake.
     */
    function stakePlanetByIds(uint256[] memory _planetIds) external {
        require(
            _planetIds.length + stakedPlanets[msg.sender].length() <= STAKE_LIMIT,
            "Can only have a max of 25 planets staked in your solar system!"
        );
        for (uint256 i = 0; i < _planetIds.length; i++) {
            _stakePlanet(_planetIds[i]);
        }
    }

    function unstakePlanetByIds(uint256[] memory _planetIds) public {
        for (uint256 i = 0; i < _planetIds.length; i++) {
            _unstakePlanet(_planetIds[i]);
        }
    }

    function claimRewardsByIds(uint256[] memory _planetIds) external {
        uint256 runningPePeAllowance;

        for (uint256 i = 0; i < _planetIds.length; i++) {
            uint256 thisPlanetId = _planetIds[i];
            require(
                stakedPlanets[msg.sender].contains(thisPlanetId),
                "Can only claim from a planet in your solar system!"
            );
            runningPePeAllowance += getPrimordialOwedToThisPlanet(thisPlanetId);

            planetClaimTimes[thisPlanetId] = block.timestamp;
        }
        _mint(msg.sender, runningPePeAllowance);
    }

    function claimAllRewards() external {
        uint256 runningPePeAllowance;

        for (uint256 i = 0; i < stakedPlanets[msg.sender].length(); i++) {
            uint256 thisPlanetId = stakedPlanets[msg.sender].at(i);
            runningPePeAllowance += getPrimordialOwedToThisPlanet(thisPlanetId);

            planetClaimTimes[thisPlanetId] = block.timestamp;
        }
        _mint(msg.sender, runningPePeAllowance);
    }

    function unstakeAll() external {
        unstakePlanetByIds(stakedPlanets[msg.sender].values());
    }

    function _stakePlanet(uint256 _planetId) internal onlyPlanetOwner(_planetId) {
        //Transfer their token
        IERC721Enumerable(primordialplanetsAddress).transferFrom(
            msg.sender,
            address(this),
            _planetId
        );

        // Add the planet to the owner's solar system
        stakedPlanets[msg.sender].add(_planetId);

        //Set this planetId timestamp to now
        planetStakeTimes[_planetId] = block.timestamp;
        planetClaimTimes[_planetId] = 0;
    }

    function _unstakePlanet(uint256 _planetId)
        internal
        onlyPlanetStaker(_planetId)
    {
        uint256 primordialOwedToThisPlanet = getPrimordialOwedToThisPlanet(_planetId);
        _mint(msg.sender, primordialOwedToThisPlanet);

        IERC721(primordialplanetsAddress).transferFrom(
            address(this),
            msg.sender,
            _planetId
        );

        stakedPlanets[msg.sender].remove(_planetId);
    }

    function mint(address to, uint256 amount) public {
        require(hasRole(MINTER_ROLE, msg.sender), "PrimordialPePe: must have minter role to mint");
        _mint(to, amount);
    }

    // GETTERS

    function getStakedPlanetData(address _address)
        external
        view
        returns (StakedPlanet[] memory)
    {
        uint256[] memory ids = stakedPlanets[_address].values();
        StakedPlanet[] memory stakedPlanet = new StakedPlanet[](ids.length);
        for (uint256 index = 0; index < ids.length; index++) {
            uint256 _planetId = ids[index];
            stakedPlanet[index] = StakedPlanet(
                _planetId,
                planetStakeTimes[_planetId],
                getPlanetPrimordialEmission(_planetId)
            );
        }

        return stakedPlanet;
    }

    function tokensStaked(address _address)
        external
        view
        returns (uint256[] memory)
    {
        return stakedPlanets[_address].values();
    }

    function stakedPlanetQuantity(address _address)
        external
        view
        returns (uint256)
    {
        return stakedPlanets[_address].length();
    }

    function getPrimordialOwedToThisPlanet(uint256 _planetId)
        public
        view
        returns (uint256)
    {
        uint256 elapsedTime = block.timestamp - planetStakeTimes[_planetId];
        uint256 elapsedDays = elapsedTime < 1 days ? 0 : elapsedTime / 1 days;
        uint256 leftoverSeconds = elapsedTime - elapsedDays * 1 days;

        if (planetClaimTimes[_planetId] == 0) {
            return _calculatePrimordial(elapsedDays, leftoverSeconds);
        }

        uint256 elapsedTimeSinceClaim = planetClaimTimes[_planetId] -
            planetStakeTimes[_planetId];
        uint256 elapsedDaysSinceClaim = elapsedTimeSinceClaim < 1 days
            ? 0
            : elapsedTimeSinceClaim / 1 days;
        uint256 leftoverSecondsSinceClaim = elapsedTimeSinceClaim -
            elapsedDaysSinceClaim *
            1 days;

        return
            _calculatePrimordial(elapsedDays, leftoverSeconds) -
            _calculatePrimordial(elapsedDaysSinceClaim, leftoverSecondsSinceClaim);
    }

    function getTotalRewardsForUser(address _address)
        external
        view
        returns (uint256)
    {
        uint256 runningPrimordialTotal;
        uint256[] memory planetIds = stakedPlanets[_address].values();
        for (uint256 i = 0; i < planetIds.length; i++) {
            runningPrimordialTotal += getPrimordialOwedToThisPlanet(planetIds[i]);
        }
        return runningPrimordialTotal;
    }

    function getPlanetPrimordialEmission(uint256 _planetId)
        public
        view
        returns (uint256)
    {
        uint256 elapsedTime = block.timestamp - planetStakeTimes[_planetId];
        uint256 elapsedDays = elapsedTime < 1 days ? 0 : elapsedTime / 1 days;
        return _primordialDailyIncrement(elapsedDays);
    }

    function getMinterCount() public view returns (uint256) {
        return minters.length();
    }

    function getMinter(uint256 index) public view returns (address) {
        require(index < minters.length(), "Index out of range");
        return minters.at(index);
    }

    function getAdminCount() public view returns (uint256) {
        return admins.length();
    }

    function getAdmin(uint256 index) public view returns (address) {
        require(index < admins.length(), "Index out of range");
        return admins.at(index);
    }

    function _calculatePrimordial(uint256 _days, uint256 _leftoverSeconds)
        internal
        pure
        returns (uint256)
    {
        uint256 progressiveDays = Math.min(_days, 100);
        uint256 progressiveReward = progressiveDays == 0
            ? 0
            : (progressiveDays *
                (2525 ether + 25 ether * (progressiveDays - 1) + 2525 ether)) /
                2;

        uint256 dailyIncrement = _primordialDailyIncrement(_days);
        uint256 leftoverReward = _leftoverSeconds > 0
            ? (dailyIncrement * _leftoverSeconds) / 1 days
            : 0;

        if (_days <= 100) {
            return progressiveReward + leftoverReward;
        }
        return progressiveReward + (_days - 100) * 5000 ether + leftoverReward;
    }

    function _primordialDailyIncrement(uint256 _days)
        internal
        pure
        returns (uint256)
    {
        return _days > 100 ? 5000 ether : 2500 ether + _days * 25 ether;
    }

    // ACCESS CONTROL FUNCTIONS

    function grantAdminRole(address account) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "PrimordialPePe: must have admin role to grant new admin");
        grantRole(DEFAULT_ADMIN_ROLE, account);
        admins.add(account);
    }

    function revokeAdminRole(address account) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "PrimordialPePe: must have admin role to revoke admin");
        revokeRole(DEFAULT_ADMIN_ROLE, account);
        admins.remove(account);
    }

    function grantMinterRole(address account) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "PrimordialPePe: must have admin role to grant minter role");
        grantRole(MINTER_ROLE, account);
        minters.add(account);
    }

    function revokeMinterRole(address account) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "PrimordialPePe: must have admin role to revoke minter role");
        revokeRole(MINTER_ROLE, account);
        minters.remove(account);
    }

    // MINING RIG

    function activate() external payable {
        require(allowed_pepe_miner == address(0) || allowed_pond_miner == address(0), "Miners activated");
        require(msg.sender != allowed_pepe_miner, "Miner already activated");
        require(minable == false, "INVALID");

        uint256 mintAmount = 1000000 ether;

        if (allowed_pepe_miner == address(0)) {
            allowed_pepe_miner = msg.sender;
            _mint(allowed_pepe_miner, mintAmount);
        } else {
            allowed_pond_miner = msg.sender;
            _mint(allowed_pond_miner, mintAmount);
            minable = true; 
        }
    }

    function mintSupplyFromMinedLP(
        address miner,
        uint256 value
    ) external payable {
        require(minable == true, "INVALID");
        require(msg.sender == allowed_pepe_miner || msg.sender == allowed_pond_miner, "INVALID");

        uint _supply = totalSupply();
        uint _calculated = _supply + value;

        require(_calculated <= max_rig_supply, "EXCEEDS MAX");
        _mint(miner, value);
    }

    // OWNER FUNCTIONS

    function setAddresses(address _primordialplanetsAddress)
        public
        onlyOwner
    {
        primordialplanetsAddress = _primordialplanetsAddress;
    }

    // MODIFIERS

    modifier onlyPlanetOwner(uint256 _planetId) {
        require(
            IERC721Enumerable(primordialplanetsAddress).ownerOf(_planetId) == msg.sender,
            "Can only stake planets in your domain!"
        );
        _;
    }

    modifier onlyPlanetStaker(uint256 _planetId) {
        require(
            stakedPlanets[msg.sender].contains(_planetId),
            "Can only unstake planets in your solar system!"
        );
        _;
    }
}

contracts/PrimordialPlanets.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "./interfaces/IPrimordialPePe.sol";
import "./libraries/PrimordialPlanetsLibrary.sol";

contract PrimordialPlanets is ERC721Enumerable {
    using PrimordialPlanetsLibrary for uint8;

    struct Trait {
        string traitName;
        string traitType;
        string pixels;
        uint256 pixelCount;
    }


    //Mappings
    mapping(uint256 => Trait[]) public traitTypes;
    mapping(string => bool) hashToMinted;
    mapping(uint256 => string) internal tokenIdToHash;

    //uint256s
    uint256 MAX_SUPPLY = 10000;
    uint256 MINTS_PER_TIER = 2000;
    uint256 SEED_NONCE = 0;

    //string arrays
    string[] LETTERS = [
        "a",
        "b",
        "c",
        "d",
        "e",
        "f",
        "g",
        "h",
        "i",
        "j",
        "k",
        "l",
        "m",
        "n",
        "o",
        "p",
        "q",
        "r",
        "s",
        "t",
        "u",
        "v",
        "w",
        "x",
        "y",
        "z"
    ];

    //uint arrays
    uint16[][8] TIERS;

    //address
    address primordialPePeAddress;
    address _owner;

    //bool
    bool mintEnabled;

    constructor() ERC721("PrimordialPlanets", "PLANETS") {
        _owner = msg.sender;

        //Declare all the rarity tiers
        //Satellites
        TIERS[0] = [500, 500, 700, 700, 1100, 1200, 1300, 1000, 1500, 2500];
        //Rings
        TIERS[1] = [300, 400, 500, 800, 800, 1000, 1000, 1200, 1500, 2500];
        //Atmosphere 
        TIERS[2] = [800, 1500, 1500, 1500, 1500, 1500, 1700];
        //Planet Type by Composition
        TIERS[3] = [200, 300, 500, 500, 500, 500, 500, 500, 500, 500, 600, 600, 700, 700, 700, 800, 1400];
        //Crust Resources
        TIERS[4] = [200, 500, 500, 600, 700, 1300, 1300, 1500, 1500, 1900];
        //Mantle
        TIERS[5] = [500, 1000, 1000, 1500, 1500, 2000, 2500];
        //Core
        TIERS[6] = [200, 300, 700, 700, 700, 1200, 1300, 1300, 1300, 2300];
        //Background
        TIERS[7] = [500, 1000, 1500, 2000, 5000];
    }

    /*
    ___  ____       _   _              ______                _   _                 
    |  \/  (_)     | | (_)             |  ___|              | | (_)                
    | .  . |_ _ __ | |_ _ _ __   __ _  | |_ _   _ _ __   ___| |_ _  ___  _ __  ___ 
    | |\/| | | '_ \| __| | '_ \ / _` | |  _| | | | '_ \ / __| __| |/ _ \| '_ \/ __|
    | |  | | | | | | |_| | | | | (_| | | | | |_| | | | | (__| |_| | (_) | | | \__ \
    \_|  |_/_|_| |_|\__|_|_| |_|\__, | \_|  \__,_|_| |_|\___|\__|_|\___/|_| |_|___/
                                 __/ |                                             
                                |___/                                              
   */

    /**
     * @dev Converts a digit from 0 - 10000 into its corresponding rarity based on the given rarity tier.
     * @param _randinput The input from 0 - 10000 to use for rarity gen.
     * @param _rarityTier The tier to use.
     */
    function rarityGen(uint256 _randinput, uint8 _rarityTier)
        internal
        view
        returns (string memory)
    {
        uint16 currentLowerBound = 0;
        for (uint8 i = 0; i < TIERS[_rarityTier].length; i++) {
            uint16 thisPercentage = TIERS[_rarityTier][i];
            if (
                _randinput >= currentLowerBound &&
                _randinput < currentLowerBound + thisPercentage
            ) return i.toString();
            currentLowerBound = currentLowerBound + thisPercentage;
        }

        revert();
    }

    /**
     * @dev Generates a 9 digit hash from a tokenId, address, and random number.
     * @param _t The token id to be used within the hash.
     * @param _a The address to be used within the hash.
     * @param _c The custom nonce to be used within the hash.
     */
    function hash(
        uint256 _t,
        address _a,
        uint256 _c
    ) internal returns (string memory) {
        require(_c < 10);

        // This will generate a 9 character string.
        //The last 8 digits are random, the first is 0, due to the planet not being burned.
        string memory currentHash = "0";

        for (uint8 i = 0; i < 8; i++) {
            SEED_NONCE++;
            uint16 _randinput = uint16(
                uint256(
                    keccak256(
                        abi.encodePacked(
                            block.timestamp,
                            block.prevrandao,
                            _t,
                            _a,
                            _c,
                            SEED_NONCE
                        )
                    )
                ) % 10000
            );

            currentHash = string(
                abi.encodePacked(currentHash, rarityGen(_randinput, i))
            );
        }

        if (hashToMinted[currentHash]) return hash(_t, _a, _c + 1);

        return currentHash;
    }

    /**
     * @dev Returns the current primordialPePe cost of minting.
     */
    function currentPrimordialPePeCost() public view returns (uint256) {
        uint256 _totalSupply = totalSupply();

        if (_totalSupply <= 2000) return 0;
        if (_totalSupply > 2000 && _totalSupply <= 4000)
            return 1000000000000000000;
        if (_totalSupply > 4000 && _totalSupply <= 6000)
            return 2000000000000000000;
        if (_totalSupply > 6000 && _totalSupply <= 8000)
            return 3000000000000000000;
        if (_totalSupply > 8000 && _totalSupply <= 10000)
            return 4000000000000000000;

        revert();
    }

    /**
     * @dev Mint internal, this is to avoid code duplication.
     */
    function mintInternal() internal {
        uint256 _totalSupply = totalSupply();
        require(_totalSupply < MAX_SUPPLY);
        require(!PrimordialPlanetsLibrary.isContract(msg.sender));

        uint256 thisTokenId = _totalSupply;

        tokenIdToHash[thisTokenId] = hash(thisTokenId, msg.sender, 0);

        hashToMinted[tokenIdToHash[thisTokenId]] = true;

        _mint(msg.sender, thisTokenId);
    }

    /**
     * @dev Mints new tokens.
     */
    function mintPlanet() public {
        require (mintEnabled);
        if (totalSupply() < MINTS_PER_TIER) return mintInternal();

        //Burn this much primordialPePe
        IPrimordialPePe(primordialPePeAddress).burnFrom(msg.sender, currentPrimordialPePeCost());

        return mintInternal();
    }

    /**
     * @dev Dev team mint tokens.
     * disables automatically once dev mint cap is met and enables public mint
     */
    function devTeamMint() public onlyOwner
        {
        require (!mintEnabled);
        if (totalSupply() == 99) mintEnabled=true;
        return mintInternal();
    }

    /**
     * @dev Burns and mints new.
     * @param _tokenId The token to burn.
     */
    function burnForMint(uint256 _tokenId) public {
        require(ownerOf(_tokenId) == msg.sender);

        //Burn token
        _transfer(
            msg.sender,
            0x000000000000000000000000000000000000dEaD,
            _tokenId
        );

        mintInternal();
    }

    /*
    ______               _  ______                _   _                 
    | ___ \             | | |  ___|              | | (_)                
    | |_/ /___  __ _  __| | | |_ _   _ _ __   ___| |_ _  ___  _ __  ___ 
    |    // _ \/ _` |/ _` | |  _| | | | '_ \ / __| __| |/ _ \| '_ \/ __|
    | |\ \  __/ (_| | (_| | | | | |_| | | | | (__| |_| | (_) | | | \__ \
    \_| \_\___|\__,_|\__,_| \_|  \__,_|_| |_|\___|\__|_|\___/|_| |_|___/

    */

    /**
     * @dev Helper function to reduce pixel size within contract
     */
    function letterToNumber(string memory _inputLetter)
        internal
        view
        returns (uint8)
    {
        for (uint8 i = 0; i < LETTERS.length; i++) {
            if (
                keccak256(abi.encodePacked((LETTERS[i]))) ==
                keccak256(abi.encodePacked((_inputLetter)))
            ) return (i + 1);
        }
        revert();
    }

     /**
     * @dev Hash to SVG function
     */
    function hashToSVG(string memory _hash)
        public
        view
        returns (string memory)
    {
        string memory svgString;
        bool[24][24] memory placedPixels;

        for (uint8 i = 0; i < 9; i++) {
            uint8 thisTraitIndex = PrimordialPlanetsLibrary.parseInt(
                PrimordialPlanetsLibrary.substring(_hash, i, i + 1)
            );

            for (
                uint16 j = 0;
                j < traitTypes[i][thisTraitIndex].pixelCount;
                j++
            ) {
                string memory thisPixel = PrimordialPlanetsLibrary.substring(
                    traitTypes[i][thisTraitIndex].pixels,
                    j * 4,  
                    j * 4 + 4  
                );

                uint8 x = letterToNumber( //edit for base64 encoding
                    PrimordialPlanetsLibrary.substring(thisPixel, 0, 1)  
                );
                uint8 y = letterToNumber( // edit for base64 encoding
                    PrimordialPlanetsLibrary.substring(thisPixel, 1, 2)  
                );

                if (placedPixels[x][y]) continue;

                svgString = string(
                    abi.encodePacked(
                        svgString,
                        "<rect class='c",
                        PrimordialPlanetsLibrary.substring(thisPixel, 2, 4),  
                        "' x='",
                        x.toString(),
                        "' y='",
                        y.toString(),
                        "'/>"
                    )
                );

                placedPixels[x][y] = true;
            }
        }

        svgString = string(
            abi.encodePacked(
                '<svg id="mouse-svg" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMinYMin meet" viewBox="0 0 24 24"> ',
                svgString,
                "<style>rect{width:1px;height:1px;} #mouse-svg{shape-rendering: crispedges;} .c00{fill:#000000}...[INCLUDE ALL COLOR CODES]...</style></svg>"
            )
        );

        return svgString;
    }

    /**
     * @dev Hash to metadata function
     */
    function hashToMetadata(string memory _hash)
        public
        view
        returns (string memory)
    {
        string memory metadataString;

        for (uint8 i = 0; i < 9; i++) {
            uint8 thisTraitIndex = PrimordialPlanetsLibrary.parseInt(
                PrimordialPlanetsLibrary.substring(_hash, i, i + 1)
            );

            metadataString = string(
                abi.encodePacked(
                    metadataString,
                    '{"trait_type":"',
                    traitTypes[i][thisTraitIndex].traitType,
                    '","value":"',
                    traitTypes[i][thisTraitIndex].traitName,
                    '"}'
                )
            );

            if (i != 8)
                metadataString = string(abi.encodePacked(metadataString, ","));
        }

        return string(abi.encodePacked("[", metadataString, "]"));
    }

    /**
     * @dev Returns the SVG and metadata for a token Id
     * @param _tokenId The tokenId to return the SVG and metadata for.
     */
    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(_exists(_tokenId));

        string memory tokenHash = _tokenIdToHash(_tokenId);

        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    PrimordialPlanetsLibrary.encode(
                        bytes(
                            string(
                                abi.encodePacked(
                                    '{"name": "PrimordialPlanets #',
                                    PrimordialPlanetsLibrary.toString(_tokenId),
                                    '", "description": "PrimordialPlanets is a collection of 10,000 unique planets. All the metadata and images are generated and stored 100% on-chain. No IPFS, no API. Pure Blockchain.", "image": "data:image/svg+xml;base64,',
                                    PrimordialPlanetsLibrary.encode(
                                        bytes(hashToSVG(tokenHash))
                                    ),
                                    '","attributes":',
                                    hashToMetadata(tokenHash),
                                    "}"
                                )
                            )
                        )
                    )
                )
            );
    }

    /**
     * @dev Returns a hash for a given tokenId
     * @param _tokenId The tokenId to return the hash for.
     */
    function _tokenIdToHash(uint256 _tokenId)
        public
        view
        returns (string memory)
    {
        string memory tokenHash = tokenIdToHash[_tokenId];
        //If this is a burned token, override the previous hash
        if (ownerOf(_tokenId) == 0x000000000000000000000000000000000000dEaD) {
            tokenHash = string(
                abi.encodePacked(
                    "1",
                    PrimordialPlanetsLibrary.substring(tokenHash, 1, 9)
                )
            );
        }

        return tokenHash;
    }

    /**
     * @dev Returns the wallet of a given wallet. Mainly for ease for frontend devs.
     * @param _wallet The wallet to get the tokens of.
     */
    function walletOfOwner(address _wallet)
        public
        view
        returns (uint256[] memory)
    {
        uint256 tokenCount = balanceOf(_wallet);

        uint256[] memory tokensId = new uint256[](tokenCount);
        for (uint256 i; i < tokenCount; i++) {
            tokensId[i] = tokenOfOwnerByIndex(_wallet, i);
        }
        return tokensId;
    }

    /*
     _____                           ______                _   _                 
    |  _  |                          |  ___|              | | (_)                
    | | | |_      ___ __   ___ _ __  | |_ _   _ _ __   ___| |_ _  ___  _ __  ___ 
    | | | \ \ /\ / / '_ \ / _ \ '__| |  _| | | | '_ \ / __| __| |/ _ \| '_ \/ __|
    \ \_/ /\ V  V /| | | |  __/ |    | | | |_| | | | | (__| |_| | (_) | | | \__ \
     \___/  \_/\_/ |_| |_|\___|_|    \_|  \__,_|_| |_|\___|\__|_|\___/|_| |_|___/

    */

    /**
     * @dev Clears the traits.
     */
    function clearTraits() public onlyOwner {
        for (uint256 i = 0; i < 9; i++) {
            delete traitTypes[i];
        }
    }

    /**
     * @dev Add a trait type
     * @param _traitTypeIndex The trait type index
     * @param traits Array of traits to add
     */

    function addTraitType(uint256 _traitTypeIndex, Trait[] memory traits)
        public
        onlyOwner
    {
        for (uint256 i = 0; i < traits.length; i++) {
            traitTypes[_traitTypeIndex].push(
                Trait(
                    traits[i].traitName,
                    traits[i].traitType,
                    traits[i].pixels,
                    traits[i].pixelCount
                )
            );
        }

        return;
    }

    /**
     * @dev Sets the primordialPePe ERC20 address
     * @param _primordialPePeAddress The primordialPePe address
     */

    function setPrimordialPePeAddress(address _primordialPePeAddress) public onlyOwner {
        primordialPePeAddress = _primordialPePeAddress;
    }

    /**
     * @dev Transfers ownership
     * @param _newOwner The new owner
     */
    function transferOwnership(address _newOwner) public onlyOwner {
        _owner = _newOwner;
    }

    /**
     * @dev Modifier to only allow owner to call functions
     */
    modifier onlyOwner() {
        require(_owner == msg.sender);
        _;
    }
}

contracts/RewardBooster.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

//import "./interfaces/IPrimordialPlanets.sol";
//import "./interfaces/ICosmicImpactDescriptor.sol";

contract BoostedRewardCalculator {

    //uint arrays
    uint16[][8] TIERS;   

    address public primordialPlanetsAddr;
    address public cosmicImpactsAddr;

    constructor(address _primordialPlanetsAddr, address _cosmicImpactsAddr) {
        primordialPlanetsAddr = _primordialPlanetsAddr;
        cosmicImpactsAddr = _cosmicImpactsAddr;
        setTiers();
    }

    function setTiers() private {
        //Satellites
        TIERS[0] = [500, 500, 700, 700, 1100, 1200, 1300, 1000, 1500, 2500];
        //Rings
        TIERS[1] = [300, 400, 500, 800, 800, 1000, 1000, 1200, 1500, 2500];
        //Atmosphere 
        TIERS[2] = [800, 1500, 1500, 1500, 1500, 1500, 1700];
        //Planet Type by Composition
        TIERS[3] = [200, 300, 500, 500, 500, 500, 500, 500, 500, 500, 600, 600, 700, 700, 700, 800, 1400];
        //Crust Resources
        TIERS[4] = [200, 500, 500, 600, 700, 1300, 1300, 1500, 1500, 1900];
        //Mantle
        TIERS[5] = [500, 1000, 1000, 1500, 1500, 2000, 2500];
        //Core
        TIERS[6] = [200, 300, 700, 700, 700, 1200, 1300, 1300, 1300, 2300];
        //Background
        TIERS[7] = [500, 1000, 1500, 2000, 5000];
    }

    function setPrimordialPlanetsAddr(address newAddr) external {
        primordialPlanetsAddr = newAddr;
    }

    function setCosmicImpactsAddr(address newAddr) external {
        cosmicImpactsAddr = newAddr;
    }

    function getBoostPercentage(uint256 tokenId, address collectionAddr) public view returns (uint256) {
        require(collectionAddr == primordialPlanetsAddr || collectionAddr == cosmicImpactsAddr, "Invalid collection address");

        string memory hash;

        if (collectionAddr == primordialPlanetsAddr) {
            //IPrimordialPlanets primordialPlanets = IPrimordialPlanets(primordialPlanetsAddr);
            //hash = primordialPlanets._tokenIdToHash(tokenId);
        } else {
            //ICosmicImpactDescriptor cosmicImpacts = ICosmicImpactDescriptor(cosmicImpactsAddr);
            //hash = cosmicImpacts.tokenIdToMetadata(tokenId);
        }

        // Ensure that the hash has a consistent length. For example, if it should always be 9 characters:
        require(bytes(hash).length == 9, "Hash length is inconsistent");

        // Calculate average rarity tier
        uint256 totalRarityTier = 0;
        for (uint256 i = 1; i < bytes(hash).length; i++) {
            // Convert each character of the hash to its corresponding rarity tier value
            totalRarityTier += uint8(uint256(keccak256(abi.encodePacked(bytes(hash)[i]))) - 48); // -48 due to ASCII offset
        }
        uint256 avgRarityTier = totalRarityTier / (bytes(hash).length - 1); 

        // Return the boost based on the average rarity tier
        return TIERS[avgRarityTier][0];
    }
}

